init:
when("$model") {
    "bedrock" -> USE { dependencies("dev.langchain4j:langchain4j-bedrock:$v") }
    "anthropic" -> USE { dependencies("dev.langchain4j:langchain4j-anthropic:$v") }
    "azure" -> USE { dependencies("dev.langchain4j:langchain4j-azure-open-ai:$v") }
    "googleai" -> USE { dependencies("dev.langchain4j:langchain4j-google-ai-gemini:$v") }
    "vertexai" -> USE { dependencies("dev.langchain4j:langchain4j-vertex-ai-gemini:$v") }
    "huggingface" -> USE { dependencies("dev.langchain4j:langchain4j-hugging-face:$v") }
    "ollama" -> USE { dependencies("dev.langchain4j:langchain4j-ollama:$v") }
    "openai" -> USE { dependencies("dev.langchain4j:langchain4j-open-ai:$v") }
    "openaiofficial" -> USE { dependencies("dev.langchain4j:langchain4j-open-ai-official:$v") }
    else -> error("Not recognized dependency '$input'. Use follow models: `bedrock`, `anthropic`, `azure`, `googleai`, `vertexai`, `huggingface`, `ollama`, `openai`, `openaiofficial`")
}
